import React from "react";

import PerfectScrollbar from "react-perfect-scrollbar";

import useSidebar from "../../hooks/useSidebar";
import SidebarFooter from "./SidebarFooter";
import SidebarNav from "./SidebarNav";
import { ReactComponent as Logo } from "../../assets/img/logo.svg";
import Logoo from "../../assets/img/logoo.png";

const Sidebar = ({ items, showFooter = true }) => {
	const { isOpen } = useSidebar();

	return (
		<nav className={`sidebar ${!isOpen ? "collapsed" : ""}`}>
			<div className="sidebar-content">
				<PerfectScrollbar>
					<a className="sidebar-brand" href="/">
						{/* <Logo /> <span className="align-middle me-3">AppStack</span> style={{ height:"100px", width: "100px", borderRadius: "10px"}}*/}
						<img src={Logoo} className="rounded-circle me-2" />
					</a>

					<SidebarNav items={items} />
					{!!showFooter && <SidebarFooter />}
				</PerfectScrollbar>
			</div>
		</nav>
	);
};

export default Sidebar;
