import { createContext, useEffect, useReducer } from "react";

import axios from "../utils/axios";
import { AxiosResponse } from "axios";
import { isValidToken, setSession } from "../utils/jwt";

const INITIALIZE = "INITIALIZE";
const SIGN_IN = "SIGN_IN";
const SIGN_OUT = "SIGN_OUT";
const SIGN_UP = "SIGN_UP";

const initialState = {
	isAuthenticated: false,
	isInitialized: false,
	user: null,
};

type JWTResponse = { accessToken: string, user: string }

const JWTReducer = (state, action) => {
	switch (action.type) {
		case INITIALIZE:
			return {
				isAuthenticated: action.payload.isAuthenticated,
				isInitialized: true,
				user: action.payload.user,
			};
		case SIGN_IN:
			return {
				...state,
				isAuthenticated: true,
				user: action.payload.user,
			};
		case SIGN_OUT:
			return {
				...state,
				isAuthenticated: false,
				user: null,
			};

		case SIGN_UP:
			return {
				...state,
				isAuthenticated: true,
				user: action.payload.user,
			};

		default:
			return state;
	}
};

const AuthContext = createContext(null);

function AuthProvider({ children }) {
	const [state, dispatch] = useReducer(JWTReducer, initialState);

	useEffect(() => {
		const initialize = async () => {
			try {
				const accessToken = window.localStorage.getItem("accessToken");

				if (accessToken && isValidToken(accessToken)) {
					setSession(accessToken);

					const response = await axios.get("/api/auth/my-account");
					const { user } = response.data;

					dispatch({
						type: INITIALIZE,
						payload: {
							isAuthenticated: true,
							user,
						},
					});
				} else {
					dispatch({
						type: INITIALIZE,
						payload: {
							isAuthenticated: false,
							user: null,
						},
					});
				}
			} catch (err) {
				console.error(err);
				dispatch({
					type: INITIALIZE,
					payload: {
						isAuthenticated: false,
						user: null,
					},
				});
			}
		};

		initialize();
	}, []);

	const signIn = async (email, password) => {
		const payload = {
			email,
			password,
		};
		const response = await axios.post<typeof payload, AxiosResponse<JWTResponse>>("/api/auth/sign-in", );
		const { accessToken, user } = response.data;

		setSession(accessToken);
		dispatch({
			type: SIGN_IN,
			payload: {
				user,
			},
		});
	};

	const signOut = async () => {
		setSession(null);
		dispatch({ type: SIGN_OUT });
	};

	const signUp = async (email, password, firstName, lastName) => {
		const payload = {
			email,
			password,
			firstName,
			lastName,
		};
		const response = await axios.post<typeof payload, AxiosResponse<JWTResponse>>("/api/auth/sign-up", );
		const { accessToken, user } = response.data;

		window.localStorage.setItem("accessToken", accessToken);
		dispatch({
			type: SIGN_UP,
			payload: {
				user,
			},
		});
	};

	const resetPassword = (email) => console.log(email);

	return (
		<AuthContext.Provider
			value={{
				...state,
				method: "jwt",
				signIn,
				signOut,
				signUp,
				resetPassword,
			}}
		>
			{children}
		</AuthContext.Provider>
	);
}

export { AuthContext, AuthProvider };
